// Parse order input from agent
let orderData;
try {
  orderData = typeof $json.input === 'string' 
    ? JSON.parse($json.input) 
    : $json.input;
} catch (e) {
  orderData = $json;
}

return {
  customerId: orderData.customer_id || $json.customerId,
  products: orderData.products || [],
  customerNumber: orderData.customer_number || $json.customerNumber
};

// Calculate order total and prepare order data
const products = $input.first().json;
const orderInput = $input.last().json;

let totalAmount = 0;
const orderProducts = [];

orderInput.products.forEach(orderProd => {
  const product = products.find(p => p.id === (orderProd.id || orderProd.product_id));

  if (product) {
    const quantity = orderProd.quantity || 1;
    const itemTotal = parseFloat(product.price) * quantity;
    totalAmount += itemTotal;

    orderProducts.push({
      product_id: product.id,
      name: product.name,
      price: product.price,
      quantity: quantity,
      subtotal: itemTotal,
      type: product.product_type
    });
  }
});

// Generate unique order number
const orderNumber = `ORD-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;

// Set expiration to 24 hours from now
const expiresAt = new Date();
expiresAt.setHours(expiresAt.getHours() + 24);

return {
  orderNumber: orderNumber,
  customerId: orderInput.customerId,
  customerNumber: orderInput.customerNumber,
  totalAmount: totalAmount.toFixed(2),
  products: orderProducts,
  expiresAt: expiresAt.toISOString(),
  status: 'pending'
};

// Generate invoice details
