parameters.jsCode:
// Parse payment submission
let paymentData;
try {
  paymentData = typeof $json.input === 'string' 
    ? JSON.parse($json.input) 
    : $json.input;
} catch (e) {
  paymentData = $json;
}

return {
  orderNumber: paymentData.order_number || paymentData.orderNumber,
  paymentReference: paymentData.payment_reference || paymentData.paymentReference,
  customerId: paymentData.customer_id || $json.customerId
};

// Prepare notification for shop owner
const order = $json;

const notification = `
üîî NEW PAYMENT NOTIFICATION
